import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import Comparison from "../components/Comparison";

function Prioritize() {
  const [index1, setIndex1] = useState(0);
  const [index2, setIndex2] = useState(1);
  const [items, setItems] = useState([]);
  const [comparisons, setComparisons] = useState([]);
  const [priority, setPriority] = useState([]);
  const { id } = useParams();

  useEffect(() => {
    fetch(`http://localhost:3000/lists/${id}/list_items`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((response) => response.json())
      .then((data) => {
        debugger;
        setItems(data);
        setIndex2(index1 + 1);
      });
  }, []);


  const handleCompare = (winner, loser) => {
    debugger;
    setPriority([...priority, winner, loser]);
    if (index2 < items.length - 1) {
      setIndex2(index2 + 1);
    } else if (index1 < items.length - 1) {
      setIndex1(index1 + 1);
      setIndex2(index1 + 1);
    } else {
      alert("Comparison complete!");
    }
  };

  for (let i = 0; i < items.length; i++) {
    for (let j = i + 1; j < items.length; j++) {
      comparisons.push({ item1: items[i], item2: items[j] });
    }
  }


  if (!items) {
    return <div>Loading...</div>;
  } else {

  }


  return (
    <div className="page-background">
      <div className="title-container">
        <h2>Compare Items</h2>
      </div>
      <div className="comparison-container">
        <div className="comparison-item">
          <button onClick={() => handleCompare(items[index1], items[index2])}>
            {/* {items[index1].name} */}
          </button>
          <button onClick={() => handleCompare(items[index2], items[index1])}>
            {/* {items[index2].name} */}
          </button>
        </div>
      </div>
      <div className="priority-container">
        <h2>Priority:</h2>
        <ul>
          {priority.map((item, index) => (
            <li key={index}>{item.name}</li>
          ))}
        </ul>
      </div>
    </div>
  );

  // return (
  //   <div className="page-background">
  //     <div className="title-container">
  //       <h2>Compare Items</h2>
  //     </div>
  //     <div className="button-container">
  //       <Comparison items={items} onCompare={handleCompare} />
  //     </div>
  //     <div className="priority-container"></div>
  //   </div>
  // );
}

export default Prioritize;
