import LandingPage from './pages/landingPage';
import Lists from './pages/Lists';
import List from './pages/List';
import NotFound from './pages/notFound';
import NewList from './pages/NewList';
import SignIn from './pages/SignIn';
import SignUp from './pages/SignUp';
import Prioritize from './pages/Prioritize';
import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Route, Redirect } from 'react-router-dom';
import LogOutButton from './components/LogOutButon';
import PrivateRoute from './PrivateRoute';

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(false);

  useEffect(() => {
    const token = sessionStorage.getItem('access_token');
    if (token) {
      setIsAuthenticated(true);
    }
  }, []);

  const handleSignIn = (token) => {
    sessionStorage.setItem('access_token', token);
    setIsAuthenticated(true);
  };

  const handleSignOut = () => {
    fetch('http://localhost:3000/logout', {
      method: 'DELETE',
      headers: {
        'Content-Type': 'application/json'
      },
    }).then(() => {
      sessionStorage.removeItem('access_token');
      setIsAuthenticated(false);
      window.location.href = '/signin';
    });
  };

  return (
    <div>
      <LogOutButton onSignOut={handleSignOut} />
      <Router>
        <Switch>
          <Route path="/signin">
            <SignIn onSignIn={handleSignIn} />
          </Route>
          <Route path="/" isAuthenticated={isAuthenticated}>
            <LandingPage />
          </Route>
          <Route path="/signin">
            <SignIn onSignIn={handleSignIn} />
          </Route>
          <Route path="/signup">
            <SignUp onSignIn={handleSignIn} />
          </Route>
          <PrivateRoute path="/lists" isAuthenticated={isAuthenticated}>
            <Lists />
          </PrivateRoute>
          <PrivateRoute path="/lists/:id" isAuthenticated={isAuthenticated}>
            <List />
          </PrivateRoute>
          <PrivateRoute path="/lists/new" isAuthenticated={isAuthenticated}>
            <NewList />
          </PrivateRoute>
          <PrivateRoute path="/lists/:id/prioritize" isAuthenticated={isAuthenticated}>
            <Prioritize />
          </PrivateRoute>
          <Route path="*">
            <NotFound />
          </Route>
        </Switch>
      </Router>
    </div>
  );
}

export default App;
