import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import Comparison from "../components/Comparison";

function Prioritize() {
  const [index1, setIndex1] = useState(0);
  const [index2, setIndex2] = useState(1);
  const [items, setItems] = useState([]);
  const [comparisons, setComparisons] = useState([]);
  const { id } = useParams();

  useEffect(() => {
    fetch(`http://localhost:3000/lists/${id}/list_items`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((response) => response.json())
      .then((data) => {
        setItems(data);
        setIndex2(index1 + 1);
      });
  }, []);

  // const handleCompare = (listItemId) => {
  //   fetch(`http://localhost:3000/lists/${id}/list_items/${listItemId}/vote`, {
  //     method: "POST",
  //     headers: {
  //       "Content-Type": "application/json",
  //     },
  //     body: JSON.stringify(),
  //   })
  //     .then((response) => response.json())
  //     .then((_data) => {
  //       setComparisons([...comparisons]);
  //       setIndex1(index1 + 1);
  //       setIndex2(index2 + 1);
  //     }
  //   )
  // };

  const handleCompare = (winner, loser) => {
    setPriority([...priority, winner, loser]);
    if (index2 < items.length - 1) {
      setIndex2(index2 + 1);
    } else if (index1 < items.length - 2) {
      setIndex1(index1 + 1);
      setIndex2(index1 + 2);
    } else {
      alert("Comparison complete!");
    }
  };

  const compareItems = (item1, item2) => {
    // Compare the two items here and return the result
    // For example:
    if (item1 > item2) {
      return `${item1} is greater than ${item2}`;
    } else if (item1 < item2) {
      return `${item1} is less than ${item2}`;
    } else {
      return `${item1} is equal to ${item2}`;
    }
  };

  for (let i = 0; i < items.length; i++) {
    for (let j = i + 1; j < items.length; j++) {
      comparisons.push({ item1: items[i], item2: items[j] });
    }
  }

  // const compareItems = (item1, item2) => {
  //   // Compare the two items here and return the result
  //   // For example:
  //   if (item1 > item2) {
  //     return `${item1} is greater than ${item2}`;
  //   } else if (item1 < item2) {
  //     return `${item1} is less than ${item2}`;
  //   } else {
  //     return `${item1} is equal to ${item2}`;
  //   }
  // };

  if (!items) {
    return <div>Loading...</div>;
  }


return (
  <div className="page-background">
    <div className="title-container">
      <h2>Compare Items</h2>
    </div>
    <div className="comparison-container">
      {comparisons.map((comparison, index) => (
        <div key={index} className="comparison-item">
          <h3>{compareItems(comparison.item1, comparison.item2)}</h3>
          <button onClick={() => handleCompare(comparison.item1, comparison.item2)}>
            {comparison.item1.name}
          </button>
          <button onClick={() => handleCompare(comparison.item2, comparison.item1)}>
            {comparison.item2.name}
          </button>
        </div>
      ))}
    </div>
    <div className="priority-container">
      <h2>Priority:</h2>
      <ul>
        {priority.map((item, index) => (
          <li key={index}>{item.name}</li>
        ))}
      </ul>
    </div>
  </div>
);

  // return (
  //   <div className="page-background">
  //     <div className="title-container">
  //       <h2>Compare Items</h2>
  //     </div>
  //     <div className="button-container">
  //       <Comparison items={items} onCompare={handleCompare} />
  //     </div>
  //     <div className="priority-container"></div>
  //   </div>
  // );
}

export default Prioritize;
