import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import ListItem from "../components/ListItem";
import "./List.css";

function Prioritize() {
  const { id } = useParams();
  const [list, setList] = useState(null);
  const [listItems, setListItems] = useState(null);
  const [comparisons, setComparisons] = useState([]);
  const [index1, setIndex1] = useState(0);
  const [index2, setIndex2] = useState(1);

  useEffect(() => {
    fetch(`http://localhost:3000/lists/${id}`)
      .then(response => response.json())
      .then(data => {
        setList(data.list);
        setListItems(data.list_items);
      });
  }, [id]);

  const handleCompare = (event, winnerId, loserId) => {
    event.preventDefault();

    fetch(`http://localhost:3000/items/${winnerId}/vote`, {
      method: "PUT",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({ loser_id: loserId })
    })
      .then(response => response.json())
      .then(data => {
        setComparisons([...comparisons, data]);
        if (index2 < listItems.length - 1) {
          setIndex2(index2 + 1);
        } else if (index1 < listItems.length - 2) {
          setIndex1(index1 + 1);
          setIndex2(index1 + 2);
        } else {
          alert("Comparison complete!");
        }
      });
  };

  if (!list) {
    return <div>Loading...</div>;
  }

  return (
    <div className="page-background">
      <div>
        <h1>{list.name}</h1>
        {listItems ? (
          <div>
            <h2>Comparisons:</h2>
            <ul>
              {comparisons.map((comparison, index) => (
                <li key={index}>{comparison}</li>
              ))}
            </ul>
            <h2>Compare:</h2>
            <div>
              <span>{listItems[index1].name}</span>
              <button onClick={(event) => handleCompare(event, listItems[index1].id, listItems[index2].id)}>vs</button>
              <span>{listItems[index2].name}</span>
            </div>
            <h2>List Items:</h2>
            <ul>
              {listItems.map(item => (
                <ListItem key={item.id} item={item} onClick={() => alert(`You clicked on ${item.name}`)} />
              ))}
            </ul>
          </div>
        ) : null}
      </div>
      <div>
        <button onClick={() => window.location.href = `/lists/${id}/edit`}>Edit List</button>
      </div>
    </div>
  );
}

export default Prioritize;
