import React, { useState, useEffect } from "react";

function Proritize() {
  const [index1, setIndex1] = useState(0);
  const [index2, setIndex2] = useState(1);
  const [items, setItems] = useState([]);
  const [comparisons, setComparisons] = useState([]);

  useEffect(() => {
    fetch("http://localhost:3000/list_items", {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((response) => response.json())
      .then((data) => {
        debugger;
        setItems(data);
        setIndex2(index1 + 1);
      });
  }, []);

  const handleCompare = (event) => {
    event.preventDefault();

    const item1 = items[index1];
    const item2 = items[index2];

    const result = compareItems(item1, item2);

    setComparisons([...comparisons, result]);

    if (index2 < items.length - 1) {
      setIndex2(index2 + 1);
    } else if (index1 < items.length - 2) {
      setIndex1(index1 + 1);
      setIndex2(index1 + 2);
    } else {
      alert("Comparison complete!");
    }
  };

  const compareItems = (item1, item2) => {
    // Compare the two items here and return the result
    // For example:
    if (item1 > item2) {
      return `${item1} is greater than ${item2}`;
    } else if (item1 < item2) {
      return `${item1} is less than ${item2}`;
    } else {
      return `${item1} is equal to ${item2}`;
    }
  };

  return (
    <div>
      <h2>Compare Items</h2>
      <p>
        Comparing {items[index1]} and {items[index2]}
      </p>
      <button onClick={handleCompare}>Compare</button>
      <ul>
        {comparisons.map((comparison, index) => (
          <li key={index}>{comparison}</li>
        ))}
      </ul>
    </div>
  );
}

export default Compare;
