import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import CompareButton from "../components/CompareButton";
import SaveButton from "../components/SaveButton";

function Prioritize() {
  const [index1, setIndex1] = useState(0);
  const [index2, setIndex2] = useState(1);
  const [items, setItems] = useState([]);
  const [comparisons, setComparisons] = useState([]);
  const [priority, setPriority] = useState([]);
  const [sorted, setSorted] = useState([]);
  const { id } = useParams();

  useEffect(() => {
    fetch(`http://localhost:3000/lists/${id}/list_items`, {
      method: "GET",
      headers: {
        "Content-Type": "application/json",
      },
    })
      .then((response) => response.json())
      .then((data) => {
        setItems(data);
        setIndex2(index1 + 1);
      });
  }, []);

// count the number of times each item is selected and sort them in descending order
  useEffect(() => {
    let counts = {};
    priority.forEach(function (x) {
      counts[x.id] = (counts[x.id] || 0) + 1;
    });
    let sorted = Object.keys(counts).sort(function (a, b) {
      return counts[b] - counts[a];
    });
    let sortedItems = [];
    // set a votes value for the number of times each item is selected
    sorted.forEach((id) => {
      let item = items.find((item) => item.id == id);
      item.votes = counts[id];
      sortedItems.push(item);
    });
    setSorted(sortedItems);
  }
  , [priority]);


  const handleCompare = (winner) => {
    setPriority([...priority, winner]);
    // iterate through each item and compare each to eachother but never the same item

    if (index2 < items.length - 1) {
      setIndex2(index2 + 1);
    } else {
      setIndex1(index1 + 1);
      setIndex2(index1 + 2);
    }

  };

  for (let i = 0; i < items.length; i++) {
    for (let j = i + 1; j < items.length; j++) {
      comparisons.push({ item1: items[i], item2: items[j] });
    }
  }


  if (!items) {
    return <div>Loading...</div>;
  } else {
    return (
      <div className="page-background">
        <div className="title-container">
          <h2>Compare Items</h2>
        </div>
        <div className="comparison-container">
          <div className="comparison-item">
            <CompareButton item={items[index1]} onCompare={handleCompare} />
            <CompareButton item={items[index2]} onCompare={handleCompare} />
          </div>
        </div>
        <div className="priority-container">
          <h2>Priority:</h2>
          <ul>
            {sorted.map((item, index) => (
              <li key={index}>{item.name} - {item.votes}</li>
            ))}
          </ul>
        </div>
      </div>
    );
  }



  // return (
  //   <div className="page-background">
  //     <div className="title-container">
  //       <h2>Compare Items</h2>
  //     </div>
  //     <div className="button-container">
  //       <Comparison items={items} onCompare={handleCompare} />
  //     </div>
  //     <div className="priority-container"></div>
  //   </div>
  // );
}

export default Prioritize;
