import React, { useState } from "react";
import "./Comparison.css";

function Comparison({ items, onCompare }) {
  const [index1, setIndex1] = useState(0);
  const [index2, setIndex2] = useState(1);

  const handleCompare = (event, winner, loser) => {
    event.preventDefault();
    onCompare(winner, loser);
    if (index2 < items.length - 1) {
      setIndex2(index2 + 1);
    } else if (index1 < items.length - 2) {
      setIndex1(index1 + 1);
      setIndex2(index1 + 2);
    } else {
      alert("Comparison complete!");
    }
  };

  if (!items) {
    return <div>Loading...</div>;
  }

  return (
    <div className="comparison-container">
      <div className="comparison-item">
        <h3>{items[index1].name}</h3>
        <button onClick={(event) => handleCompare(event, items[index1], items[index2])}>vs</button>
        <h3>{items[index2].name}</h3>
      </div>
    </div>
  );
}

export default Comparison;
