import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import ListItem from "../components/ListItem";
import "./List.css";

function List() {
  const { id } = useParams();
  const [list, setList] = useState(null);
  const [listItems, setListItems] = useState(null);
  const [newItemName, setNewItemName] = useState("");
  const [showNewItem, setShowNewItem] = useState(false);

  useEffect(() => {
    fetch(`http://localhost:3000/lists/${id}`)
      .then(response => response.json())
      .then(data => {
        setList(data.list);
        setListItems(data.list_items);
      });
  }, [id]);

  const handleAddItem = (event) => {
    event.preventDefault();

    fetch(`http://localhost:3000/lists/${id}/items`, {
      method: "POST",
      headers: {
        "Content-Type": "application/json"
      },
      body: JSON.stringify({ name: newItemName })
    })
      .then(response => response.json())
      .then(data => {
        setListItems([...listItems, data]);
        setNewItemName("");
        setShowNewItem(true);
        setTimeout(() => setShowNewItem(false), 2000);
      });
  };

  if (!list) {
    return <div>Loading...</div>;
  }

  return (
    <div className="page-background">
      <div>
        <h1>{list.name}</h1>
        <form onSubmit={handleAddItem}>
          <input
            type="text"
            value={newItemName}
            onChange={(event) => setNewItemName(event.target.value)}
            placeholder="Enter new item name"
          />
          <button type="submit">Add Item</button>
        </form>
        {showNewItem && (
          <div className="new-item-animation">
            <span className="new-item-text">{newItemName}</span>
          </div>
        )}
        {listItems ? listItems.map(item => (
          <ListItem key={item.id} item={item} />
        )) : null}
      </div>
      <div>
        <button onClick={() => window.location.href = `/lists/${id}/edit`}>Edit List</button>
      </div>
    </div>
  );
}

export default List;
